resource "aws_s3_bucket_policy" "AssumedRolesBucketPolicy" {
  bucket = aws_s3_bucket.AssumedRolesBucket.id

  policy = jsonencode({
    Version = "2012-10-17"
    Statement = [
      {
        Action   = "s3:GetObject"
        Effect   = "Allow"
        Resource = "${aws_s3_bucket.AssumedRolesBucket.arn}/*"
        Principal = "*"
      }
    ]
  })
}
